import axios from 'axios'
const CryptoJS = require("crypto-js");
export default function withStoredHeaders() {
  axios.defaults.xsrfHeaderName = "X-CSRFTOKEN";
  axios.defaults.xsrfCookieName = "csrftoken";
  const token = localStorage.getItem('token');
  const bytes  = CryptoJS.AES.decrypt(token, '4sri');
  const decryptedData = JSON.parse(bytes.toString(CryptoJS.enc.Utf8));

  return axios.create({
  	baseURL: 'http://localhost:4000/',
    headers: {
      'Authorization' : `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6MTIsImlhdCI6MTU2NjIxNzI0NCwiZXhwIjoxNTY2MzAzNjQ0fQ.KjuR7yzvTfg4_Di3ECWecy2K1gVAxVa7hkg6oo_MWak`,
    },
  })
}
