import React, { Component } from 'react';
import { Card, CardBody, CardHeader, Col, Row, Table } from 'reactstrap';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import DateRangePicker from 'react-bootstrap-daterangepicker';
import 'bootstrap-daterangepicker/daterangepicker.css';
import usersData from './UsersData'

class User extends Component {

  render() {
    const options = {
      expandRowBgColor: '#efefef',
      expandAll: true,
      paginationShowsTotal: this.renderShowsTotal,
    };

    const user = usersData.find( user => user.id.toString() === this.props.match.params.id)

    const userDetails = user ? Object.entries(user) : [['id', (<span><i className="text-muted icon-ban"></i> Not found</span>)]]

    return (
      <div className="animated fadeIn">
        <Row>
          <Col lg={6}>
            <Card>
              <CardHeader>
                <strong><i className="icon-info pr-1"></i>User id: {this.props.match.params.id}</strong>
              </CardHeader>
              <CardBody>
                    <BootstrapTable data={ usersData }
                       options={ options }
                       expandableRow={ this.isExpandableRow }
                       expandComponent={ this.expandComponent }
                       expandColumnOptions={ { expandColumnVisible: true } }
                       pagination
                       search>
                      <TableHeaderColumn width="50" ref='idFetch' dataField='idFetch' isKey={ true }>No</TableHeaderColumn>
                      <TableHeaderColumn ref='phone' dataField='phone' filter={ { type: 'TextFilter', placeholder: 'Enter Mobile Number' } }>Mobile Number</TableHeaderColumn>
                      <TableHeaderColumn ref='nickname' dataField='nickname' filter={ { type: 'TextFilter', placeholder: 'Masukkan Nama' } }>Name</TableHeaderColumn>
                    </BootstrapTable>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    )
  }
}

export default User;
