import React, { Component } from 'react';
import { Card, CardBody, CardHeader, Col, Row, Table, Button } from 'reactstrap';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import DateRangePicker from 'react-bootstrap-daterangepicker';
import 'bootstrap-daterangepicker/daterangepicker.css';
import usersData from './UsersData'
import * as moment from 'moment/moment';
import { fetchingDataAPI } from '../../utils/AxiosMethod';
let contentData = []
class User extends Component {
 constructor(props) {
   super(props);
   this.state = {
        data : [],
        cellEditProp : {
          mode: 'click',
          blurToSave: true,
          afterSaveCell: this.onAfterSaveCell // a hook for after saving cell
        },
   };
   this.onAfterSaveCell = this.onAfterSaveCell.bind(this)
   this.jobStatusValidator = this.jobStatusValidator.bind(this)
 }

 onAfterSaveCell = (row, cellName, cellValue) => {
    console.log(row, cellName, cellValue)
  }
  invalidJobStatus = (cell, row) => {
    return 'invalid-jobstatus-class';
  }
  jobStatusValidator = (value) => {
    console.log(value)
    const nan = isNaN(parseInt(value, 10));
    if (nan) {
      return 'Harus Integer';
    }
    return false;
  }

 componentDidMount(){
    fetchingDataAPI('user').then(result => {
      this.setState({
        data : result
      })
    }).catch(err => console.log(err));
 }
  render() {
    const { cellEditProp , data } = this.state
    const selectRowProp = {
      mode: 'checkbox',
      clickToSelect: true,
      showOnlySelected: true,
      onlyUnselectVisible: true,
      columnWidth :'60px',
      bgColor: 'lightblue',
    };

    const options = {
      paginationShowsTotal: this.renderShowsTotal,
      exportCSVBtn:this.createCustomExportCSV,
      showSelectedOnlyBtn: this.ShowSelectedOnlyButton,
      onDeleteRow  : this.handleDeleteButtonClick,
      onRowClick: function(row, columnIndex, rowIndex, e){
        // alert(`Kamu menekan sekali baris dengan Device ID: ${row.id} ,${columnIndex},${rowIndex}`);
        console.log(e);
        }

    };

    const user = usersData.find( user => user.id.toString() === this.props.match.params.id)

    const userDetails = user ? Object.entries(user) : [['id', (<span><i className="text-muted icon-ban"></i> Not found</span>)]]

    return (
      <div className="animated fadeIn">
        <Row>
          <Col xs="12" lg="12">
            <Card>
              <CardHeader>
                <strong><i className="icon-info pr-1"></i>User id: {this.props.match.params.id}</strong>
              </CardHeader>
              <CardBody>
                 <div className='form-group'>
                  <DateRangePicker 
                    startDate={this.state.startDateDR} 
                    endDate={this.state.endDate}
                    onEvent={this.handleChangeDatePicker}
                    showWeekNumbers
                    >
                    <Button onClick={this.onClick}>
                      <i className="fa fa-calendar"/>
                      <span> {this.state.labelCal}</span>                          
                    </Button>            
                  </DateRangePicker>
                </div>
                 {/*<button onClick={this.getSelectedRowKeys.bind(this)}>Get selected row keys</button>*/}
                <BootstrapTable data={ data } remote={ this.remote } selectRow={ selectRowProp } cellEdit={ cellEditProp } height='100%' pagination scrollTop={ 'Bottom' } hover condensed striped exportCSV
                   expandableRow={ this.isExpandableRow } expandComponent={ this.expandComponent } options={ options } deleteRow={ true }>
                  <TableHeaderColumn ref='id' dataField='id' isKey={ true } dataAlign='center'>ID</TableHeaderColumn>
                  <TableHeaderColumn ref='first_name' dataField='first_name' filter={ { type: 'TextFilter' } } headerAlign='center' editable={ { type: 'textarea' , validator: this.jobStatusValidator } } invalidEditColumnClassName={ this.invalidJobStatus }>First Name</TableHeaderColumn>
                  <TableHeaderColumn ref='last_name' dataField='last_name' filter={ { type: 'TextFilter'} } headerAlign='center' tdStyle={ { whiteSpace: 'normal' } } editable={ { type: 'textarea' , validator: this.jobStatusValidator } } invalidEditColumnClassName={ this.invalidJobStatus }>Last Name</TableHeaderColumn>
                  <TableHeaderColumn ref='email' dataField='email' filter={ { type: 'TextFilter' } } headerAlign='center' tdStyle={ { whiteSpace: 'normal' } } editable={ { type: 'textarea' , validator: this.jobStatusValidator } } invalidEditColumnClassName={ this.invalidJobStatus }>email</TableHeaderColumn>
                  <TableHeaderColumn ref='status' dataField='status' filter={ { type: 'TextFilter' } } headerAlign='center'>status</TableHeaderColumn>
                  <TableHeaderColumn ref='created' dataField='created' filter={ { type: 'TextFilter' } } headerAlign='center'>createtime</TableHeaderColumn>
                </BootstrapTable>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    )
  }
}

export default User;
