    putDataAPI('password',{password, email}).then(result => {
      if(result.response === 200){
        this.setState({
                 postData : {
                      isFetching : true,
                      status : 'success',
                      notification : `Success Generate New Password`
                  }
              })
      }
    }).catch( err => console.log(err))          
        }else{
              this.setState({
                 postData : {
                      isFetching : true,
                      status : 'danger',
                      notification : `Password not Match / at Least 6 Character`
