import axios from 'axios'
export const SET_FETCHING = 'SET_FETCHING';
export const SET_TOKEN= 'SET_TOKEN';
export const DELETE_TOKEN = 'DELETE_TOKEN';
export const SET_USER = 'SET_USER';
export const DELETE_USER = 'DELETE_USER';
export const SET_AUTH = 'SET_AUTH';
axios.defaults.xsrfHeaderName = "X-CSRFTOKEN";
axios.defaults.xsrfCookieName = "csrftoken";
const SECRET_KEY = '4sri'
var CryptoJS = require("crypto-js");

export function setFetching(v) {
  return { type: SET_FETCHING, state: v };
}

export function setUser(user) {
  return { type: SET_USER, user };
}

export function setAuthenticated(auth) {
  return { type: SET_AUTH, state: auth };
}

export function setToken(token) {
  return { type: SET_TOKEN , token };
}

export function deleteUser() {
  return { type: DELETE_USER };
}

export function deleteToken() {
  return { type: DELETE_TOKEN };
}

export function actionCheckLogin(values) {
   return dispatch => {
     axios.get("http://127.0.0.1:4000/profile/",{
        headers: {
          'Authorization': `Bearer ${values.token}`,
          'Secret': `${SECRET_KEY}`,
        }
      })
        .then(response => response.data)
        .then(json => {
          console.log(json)
        });
    }
  };

export function actionGetUsername(values) {
   return dispatch => {
     axios.post("http://127.0.0.1:4000/getUser",{ email : values.email }, {
        headers: {
          'Authorization': `Bearer ${values.token}`,
        }
      })
        .then(response => response.data)
        .then(json => {
          console.log(json)
           dispatch(setUser(json.user));
           localStorage.setItem("user", JSON.stringify(json.user));
           dispatch(setFetching(false));
           dispatch(setAuthenticated(true));
        });
    }
  };

export function actionTryLogin(values) {

    return dispatch => {
    dispatch(setFetching(true));
    axios.post('http://127.0.0.1:4000/login/', values)
    .then(response => response.data)
    .then(token => {
      if (token !== null) {
        dispatch(setToken({ token : token.token}))
                console.log(token)
        // dispatch(actionGetUsername(token))
        const data = { token : token.token}
        let ciphertext = data
        if(token.token){
          ciphertext = CryptoJS.AES.encrypt(JSON.stringify(data), '4sri').toString();
        }

        localStorage.setItem("token", ciphertext);
      }
    })
    .catch(err => {
      dispatch(setFetching(false));
      dispatch(setAuthenticated(false));
    });
  };
}
